import axios from "axios";
import { useState, useEffect } from "react";
import "./App.css";

function App() {
  const [coins, setCoins] = useState([]);
  const [searchCoin, setSearchCoin] = useState("");
  const [searchSymbol, setSearchSymbol] = useState("");

  useEffect(() => {
    const fetchData = async () => {
      const response = await axios.get(
        "https://api.coinstats.app/public/v1/coins?skip=0&limit=100"
      );
      setCoins(response.data.coins);
    };
    fetchData().catch(console.error);
    const interval = setInterval(() => {
      fetchData();
    }, 30000);
    return () => clearInterval(interval);
  }, []);

  console.log(coins);

  const filteredCoins = coins.filter((coin) => {
    return (
      coin.name.toLowerCase().includes(searchCoin.toLowerCase()) ||
      coin.symbol.toLowerCase().includes(searchSymbol.toLowerCase())
    );
  });

  return (
    <div className="App">
      <div>
        <input
          type="text"
          placeholder="Search cryptocurrencies"
          onChange={(e) => {
            setSearchCoin(e.target.value);
            setSearchSymbol(e.target.value);
          }}
        />
      </div>
      <table>
        <thead>
          <tr>
            <th>#</th>
            <th className="name">Name</th>
            <th className="price">Price</th>
            <th className="oneday">24 h</th>
            <th className="oneweek">7 d</th>
            <th className="marketcap">Market cap</th>
          </tr>
        </thead>
        <tbody>
          {filteredCoins?.map((coin) => {
            return (
              <tr>
                <td className="coin__rank">{coin.rank}</td>
                <td>
                  <img className="coin__img" src={coin.icon} />
                  <p>{coin.name}</p>
                  <span className="coin__symbol"> {coin.symbol} </span>
                </td>
                <td>
                  <p className="coin__price">
                    $
                    {coin.price.toLocaleString(undefined, {
                      minimumFractionDigits: 2,
                      maximumFractionDigits: 2,
                    })}
                  </p>
                </td>
                <td>
                  {coin.priceChange1d < 0 ? (
                    <p className="coin-percent red">
                      {coin.priceChange1d.toFixed(2)}%
                    </p>
                  ) : (
                    <p className="coin-percent green">
                      {coin.priceChange1d.toFixed(2)}%
                    </p>
                  )}
                </td>
                <td>
                  {coin.priceChange1w < 0 ? (
                    <p className="coin-percent red">
                      {coin.priceChange1w.toFixed(2)}%
                    </p>
                  ) : (
                    <p className="coin-percent green">
                      {coin.priceChange1w.toFixed(2)}%
                    </p>
                  )}
                </td>
                <td>
                  <p className="market__cap">
                    ${Math.round((coin.marketCap * 100) / 100).toLocaleString()}
                  </p>
                </td>
              </tr>
            );
          })}
        </tbody>
      </table>
    </div>
  );
}

export default App;
