import React from "react";

const Coins = ({
  name,
  rank,
  icon,
  price,
  symbol,
  priceChange1d,
  priceChange1w,
  marketCap,
}) => {
  return (
    <div className="card">
      <table>
        <tr>
          <td>
            <p className="coin__rank"> {rank} </p>
            <img className="coin__img" src={icon} /> {name}
            <span className="coin__symbol"> {symbol} </span>
            <p className="coin__price">
              $
              {price.toLocaleString(undefined, {
                minimumFractionDigits: 2,
                maximumFractionDigits: 2,
              })}
            </p>
          </td>
        </tr>
        <div className="coin__name"></div>
        {priceChange1d < 0 ? (
          <p className="coin-percent red">{priceChange1d.toFixed(2)}%</p>
        ) : (
          <p className="coin-percent green">{priceChange1d.toFixed(2)}%</p>
        )}
        {priceChange1w < 0 ? (
          <p className="coin-percent red">{priceChange1w.toFixed(2)}%</p>
        ) : (
          <p className="coin-percent green">{priceChange1w.toFixed(2)}%</p>
        )}
        <p className="market__cap">
          $ {Math.round((marketCap * 100) / 100).toLocaleString()}
        </p>
      </table>
    </div>
  );
};

export default Coins;
