import axios from "axios";
import { useState, useEffect } from "react";
import Coins from "./Components/Coins";
import "./App.css";

function App() {
  const [coins, setCoins] = useState([]);
  const [searchCoin, setSearchCoin] = useState("");
  const [searchSymbol, setSearchSymbol] = useState("");

  useEffect(() => {
    const fetchData = async () => {
      const response = await axios.get(
        "https://api.coinstats.app/public/v1/coins?skip=0&limit=100"
      );
      setCoins(response.data.coins);
    };
    fetchData().catch(console.error);
    const interval = setInterval(() => {
      fetchData();
    }, 10000);
    return () => clearInterval(interval);
  }, []);

  console.log(coins);

  const filteredCoins = coins.filter((coin) => {
    return (
      coin.name.toLowerCase().includes(searchCoin.toLowerCase()) ||
      coin.symbol.toLowerCase().includes(searchSymbol.toLowerCase())
    );
  });

  return (
    <div className="App">
      <div>
        <input
          type="text"
          placeholder="Search cryptocurrencies"
          onChange={(e) => {
            setSearchCoin(e.target.value);
            setSearchSymbol(e.target.value);
          }}
        />
      </div>
      <table>
        <tr>
          <div className="coin__header">
            <th>#</th>
            <th className="name">Name</th>
            <th className="price">Price</th>
            <th className="24h">24 h</th>
            <th className="7d">7 d</th>
            <th className="marketcap">Market cap</th>
          </div>
        </tr>
        <section className="card__container">
          {filteredCoins?.map((coin) => {
            return (
              <tr> <td>{coin.rank}</td> <td> <img className="coin__img" src={coin.icon} /> {coin.name} <span className="coin__symbol"> {coin.symbol} </span> </td> <td> <p className="coin__price"> $ {coin.price.toLocaleString(undefined, { minimumFractionDigits: 2, maximumFractionDigits: 2, })} </p> </td> <td> {coin.priceChange1d < 0 ? ( <p className="coin-percent red">{priceChange1d.toFixed(2)}%</p> ) : ( <p className="coin-percent green">{priceChange1d.toFixed(2)}%</p> )} </td> <td> {" "} {coin.priceChange1w < 0 ? ( <p className="coin-percent red">{priceChange1w.toFixed(2)}%</p> ) : ( <p className="coin-percent green">{priceChange1w.toFixed(2)}%</p> )} </td> <td> <p className="market__cap"> $ {Math.round((coin.marketCap * 100) / 100).toLocaleString()} </p> </td> </tr>
            );
          })}
        </section>
      </table>
    </div>
  );
}

export default App;
